import Form from 'react-bootstrap/Form';

function RangeSlider({label, maxAxis, minAxis}) {

  function handleChange(e) {
    e.preventDefault();
    e.target.value = Math.floor((e.target.value / 100) * maxAxis);
  }

  return (
    <>
      <Form.Label>{label}</Form.Label>
      <Form.Range onChange={handleChange} value={100}/>
    </>
  );
}

export default RangeSlider;
