import Form from 'react-bootstrap/Form';
import { useContext } from 'react';
import { appContext } from '../App';

function RangeSlider({label}) {
  const { maxYAxis, setMaxYAxis, setGraphMaxYAxis } = useContext(appContext);
  let newValue = 100;
  function handleChange(e) {
    e.preventDefault();
    const newGraphMaximum = Math.floor((e.target.value / 100) * maxYAxis);
    setGraphMaxYAxis(newGraphMaximum);
    newValue = e.target.value;
    console.log(newValue);
  }

  return (
    <>
      <Form.Label>{label}</Form.Label>
      <Form.Range onChange={handleChange} value={newValue}/>
    </>
  );
}

export default RangeSlider;
