import './App.css';
import {useState, useEffect} from 'react';
import axios from 'axios';
import NavBar from './components/NavBar';
import CreateGraphs from './components/CreateGraphs';
import LoadingBubble from './components/LoadingBubble';
import createFormattedTests from './utils/createFormattedTests';

function App() {
	const [data, setData] = useState({regions: []});
  const [shownRegion, setShownRegion] = useState()
  const [aggTime, setAggTime] = useState("1");
  const [toggleExclusion, setToggleExclusion] = useState(true);
  const [maxXAxis, setMaxXAxis] = useState(0);
  const [maxYAxis, setMaxYAxis] = useState(0);
  const [absoluteXAxis, setAbsoluteXAxis] = useState(0);
  const [absoluteYAxis, setAbsoluteYAxis] = useState(0);

  useEffect(() => {
    if (!shownRegion && data.regions.length > 0) {
      setShownRegion(data.regions[data.regions.length - 1]);
    }
  })

  useEffect(() => {
    const dataToSet = createFormattedTests(data, aggTime, toggleExclusion, absoluteXAxis, absoluteYAxis, setAbsoluteXAxis, setAbsoluteYAxis);
    setData(dataToSet);
  }, [toggleExclusion])

  useEffect(() => {
    if (data.regions.length > 0) {
      const dataToSet = createFormattedTests(data, aggTime, toggleExclusion, absoluteXAxis, absoluteYAxis, setAbsoluteXAxis, setAbsoluteYAxis);
      setData(dataToSet);
    } else {
      axios.get(`http://localhost:3002/data?time=${aggTime}`)
        .then(result => {
          const dataToSet = createFormattedTests(result.data, aggTime, toggleExclusion, absoluteXAxis, absoluteYAxis, setAbsoluteXAxis, setAbsoluteYAxis);
          setData(dataToSet);
        })
    }
  }, [aggTime])

  return (
    <div className="App">
      <link
        rel="stylesheet"
        href="https://cdn.jsdelivr.net/npm/bootstrap@5.2.2/dist/css/bootstrap.min.css"
        integrity="sha384-Zenh87qX5JnK2Jl0vWa8Ck2rdkQ2Bzep5IDxbcnCeuOxjzrPF/et3URy9Bv1WTRi"
        crossOrigin="anonymous"
        />
      <header>
        <NavBar regions={data.regions} setShownRegion={setShownRegion} setAggTime={setAggTime} 
          toggleExclusion={toggleExclusion} setToggleExclusion={setToggleExclusion} 
          setMaxXAxis={setMaxXAxis} setMaxYAxis={setMaxYAxis} absoluteXAxis={absoluteXAxis} 
          absoluteYAxis={absoluteYAxis} 
          />
      </header>
      <div>
        {!shownRegion ? <LoadingBubble /> : <CreateGraphs data={data} shownRegion={shownRegion} maxXAxis={maxXAxis} maxYAxis={maxYAxis} />}
      </div>
    </div>
  );
}

export default App;
