import Form from 'react-bootstrap/Form';
import { useContext } from 'react';
import { appContext } from '../App';

function RangeSlider({label}) {
  const { maxYAxis, setMaxYAxis, setGraphMaxYAxis } = useContext(appContext);

  function handleChange(e) {
    e.preventDefault();
    const newGraphMaximum = Math.floor((e.target.value / 100) * maxAxis);
    setGraphMaxYAxis(newGraphMaximum);
  }

  return (
    <>
      <Form.Label>{label}</Form.Label>
      <Form.Range onChange={handleChange} value={10000}/>
    </>
  );
}

export default RangeSlider;
