import './App.css';
import {useState, useEffect} from 'react';
import axios from 'axios';
import NavBar from './components/NavBar';
import LoadingBubble from './components/LoadingBubble';
import CreateGraphs from './components/CreateGraphs';

function App() {
	const [data, setData] = useState({regions: []});
  const [shownRegion, setShownRegion] = useState()
  const [aggTime, setAggTime] = useState("1");

  useEffect(() => {
    if (!shownRegion && data.regions.length > 0) {
      const regions = data.regions;
      setShownRegion(regions[regions.length - 1]);
    }
  }, [data])

  useEffect(() => {
    (async () => {
      const result = await axios.get(`http://localhost:3002/data?time=${aggTime}`);
      setData(result.data);
    })();
  }, [aggTime])

  useEffect(() => {
    (async () => {
      const result = await axios.get(`http://localhost:3002/data?time=${aggTime}`);
      setData(result.data);
      console.log(data);
      if (!shownRegion && data.regions.length > 0) {
        const regions = data.regions;
        setShownRegion(regions[regions.length - 1]);
      }
    })();
  }, []);
  return (
    <div className="App">
      <link
        rel="stylesheet"
        href="https://cdn.jsdelivr.net/npm/bootstrap@5.2.2/dist/css/bootstrap.min.css"
        integrity="sha384-Zenh87qX5JnK2Jl0vWa8Ck2rdkQ2Bzep5IDxbcnCeuOxjzrPF/et3URy9Bv1WTRi"
        crossOrigin="anonymous"
        />
      <header>
        <NavBar regions={data.regions} setShownRegion={setShownRegion} setAggTime={setAggTime} />
      </header>
      <div>
        {!shownRegion ? <LoadingBubble /> : <CreateGraphs data={data} shownRegion={shownRegion} />}
      </div>
    </div>
  );
}

export default App;
