import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import { useContext } from 'react';
import { appContext } from '../App';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);



function LineGraph(data, region) {
  const { minXAxis, minYAxis, maxXAxis, maxYAxis, graphMaxYAxis } = useContext(appContext);
  const options =  { 
    scales: {
      yAxes: {
        title: {
          display: true,
          text: "Milliseconds",
          font: {
            size: 15
          }
        },
        ticks: {
          precision: 0
        }
      },
      xAxes: {
        title: {
          display: true,
          text: "Time",
          font: {
            size: 15
          }
        }
      }
    },
    plugins: {
      legend: {
        position: 'bottom',
      },
      title: {
        display: true,
        text: region,
      },
    }
  }

  const labels = data[region].tests.map(d => d.time);
  const averageLatency = data[region].tests.map(d => d.runtime);
  const lineGraphData = {
    labels,
    datasets: [
      {
        label: "Avg. Test Runtime",
        data: averageLatency,
        borderColor: 'rgb(0, 99, 132)',
        backgroundColor: 'rgb(0, 99, 132)'
      }
    ],
  };
  return <Line options={options} data={lineGraphData} />;
}
export default LineGraph;
